import 'dart:convert';
import 'package:poem_mobile/member_profile/bloc/bloc.dart';

class MemberInfoRepository {
  Future<MemberInfo> getMemberInfo(int id) async {
    await Future.delayed(Duration(seconds: 1));
    var _data =
        '{"status":200,"message":"Data fetched successfully","data":{"memberId":"1234567891234567","barcode":"data:image/png;base64,"}}';
    var res = json.decode(_data);
    return MemberInfo.fromPrefsJson(res['data'] as Map);
  }
}
